validate_doc_update:
function(newDoc, oldDoc, userCtx) {

    function require(field, message) {
      message = message || field + ' is required';
      if (!newDoc[field]) {
          throw({forbidden : message});
      }
    };

    function unchanged(field) {
      if(oldDoc && toJSON(oldDoc[field]) !== toJSON(newDoc[field])) {
        throw({forbidden : field + ' is read-only'});
      }
    };

    if(newDoc._deleted) {
      return true;
    }
    else {
      switch(newDoc.type) {
        case 'user':
          require('name');
          require('email');
          require('password');
          break;
        case 'post':
          require('text');
          require('user');
          require('dateCreated');
          break;
        case 'follow':
          require('user');
          require('follows');
          break;    
      }
    }    
  }
